li.item-list__item{margin:0px -30px;list-style:none;text-aline:left
}
.container{margin:0 auto;-webkit-box-flex:1;-ms-flex-positive:1;flex-grow:1;-webkit-box-sizing:border-box;box-sizing:border-box;padding-top:60px;min-height:100vh
}
.container__inner{max-width:1280px;margin:10px auto
}
.post-section{max-width:100%;-webkit-box-flex:1;-ms-flex:1;flex:1
}
.line-section{max-width:60%;margin:20px auto;padding:10px
}
@media only screen and (min-width: 640px) and (max-width: 1024px){
.line-section{max-width:85%
}
}
@media only screen and (min-width: 640px) and (max-width: 800px){
.line-section{max-width:85%
}
}
@media only screen and (max-width: 640px){
.line-section{max-width:95%
}
}
.shop-section{max-width:80%;margin:20px auto;padding:10px
}
@media only screen and (min-width: 640px) and (max-width: 1024px){
.shop-section{max-width:85%
}
}
@media only screen and (min-width: 640px) and (max-width: 800px){
.shop-section{max-width:85%
}
}
@media only screen and (max-width: 640px){
.shop-section{max-width:95%
}
}
h1,h2,h3,h4,p,ul{font-family:"Source Sans Pro",sans-serif;color:#35485E
}
h1{font-size:1.6rem;margin-bottom:10px;pause:40s 60s;font-weight:400;text-align:center
}
@media only screen and (min-width: 640px){
h1{font-size:1.7rem
}
}
@media only screen and (min-width: 1024px){
h1{font-size:1.8rem
}
}
h2{font-size:1.5rem;font-weight:400
}
@media only screen and (min-width: 640px){
h2{size:1.5rem
}
}
@media only screen and (min-width: 1024px){
h2{size:1.7rem
}
}
h3{font-size:1.3rem;font-weight:300
}
@media only screen and (min-width: 640px){
h3{size:1.5rem
}
}
h4{font-size:1.6rem;font-weight:300
}
@media only screen and (min-width: 640px){
h4{size:1.8rem
}
}
p{margin:20px 0 0;font-size:100%;font-weight:300;line-height:1.4
}
@media only screen and (min-width: 640px){
p{font-size:20px
}
}
@media only screen and (min-width: 1024px){
p{font-size:22px
}
}
ul,ol{margin:2px 0 2px;font-size:100%;font-weight:300;line-height:1.4
}
@media only screen and (min-width: 640px){
ul,ol{margin:10px 0 10px;font-size:20px
}
}
@media only screen and (min-width: 1024px){
ul,ol{margin:12px 0 12px;font-size:22px
}
}
li{margin:2px 0 2px
}
@media only screen and (min-width: 640px){
li{margin:4px 0 4px
}
}
@media only screen and (min-width: 1024px){
li{margin:5px 0 5px
}
}
hr{border:none;border-top:1px solid #ddd
}
strong{font-weight:600
}
a{color:red;text-decoration:none
}
a img{border:0px
}
.icon{display:inline-block;vertical-align:middle;background-repeat:no-repeat
}
.icon--twitter{background-image:url("~assets/img/twitter.svg");background-size:24px 19px;width:24px;height:19px
}
.icon--github{background-image:url("~assets/img/github.svg");background-size:22px;width:22px;height:22px
}
.icon--gitlab{background-image:url("~assets/img/gitlab.svg");background-size:22px;width:22px;height:22px
}
.icon--facebook{background-image:url("~assets/img/facebook-logo.svg");background-size:22px;width:22px;height:22px
}
.navbar{display:block;position:fixed;z-index:10;background:rgba(255,255,255,0.8);border-bottom:1px solid #efefef;width:100%;padding:10px 0;-webkit-box-align:center;-ms-flex-align:center;align-items:center
}
.nav-target{position:absolute;top:-76px
}
.navbar__container{max-width:960px;padding:0 10px;margin:0 auto;display:-webkit-box;display:-ms-flexbox;display:flex;-ms-flex-pack:distribute;justify-content:space-around
}
.navbar__logo{background-image:url("/img/icon/katrin-logo.svg");background-size:contain;background-repeat:no-repeat;width:350px;height:50px
}
.navbar-nav__item a{text-decoration:none;color:red
}
.navbar-nav__item a.button{color:white
}
.navbar-nav{margin:0;padding:0;list-style:none
}
.navbar-nav__item{display:inline-block;vertical-align:middle;padding-left:25px;line-height:35px;text-transform:uppercase
}
.navbar-nav__item a{font-size:15px;font-weight:normal
}
.navbar__burger{display:none
}
@media screen and (max-width: 639px){
.navbar__burger{display:block;border:1px solid #34bb84;border-radius:5px;height:30px;width:40px;-webkit-box-sizing:border-box;box-sizing:border-box;padding:6px 6px;background:none
}
.navbar__burger .bar{position:relative;height:2px;margin-bottom:3px;background:#34bb84;-webkit-transition:opacity 0.5s ease, -webkit-transform 0.5s ease;transition:opacity 0.5s ease, -webkit-transform 0.5s ease;-o-transition:opacity 0.5s ease, transform 0.5s ease;transition:opacity 0.5s ease, transform 0.5s ease;transition:opacity 0.5s ease, transform 0.5s ease, -webkit-transform 0.5s ease
}
nav{position:absolute;left:100%;top:0
}
nav li{width:250px;padding-left:0 !important;padding:0
}
nav li>a{padding:15px 25px;display:block
}
nav li+li{-webkit-box-shadow:0 -10px 0 -9px #eee;box-shadow:0 -10px 0 -9px #eee
}
nav .button{margin:20px auto;display:block;max-width:calc(100% - 100px)
}
.navbar,.navbar ~ *{-webkit-transition:-webkit-transform 0.3s ease;transition:-webkit-transform 0.3s ease;-o-transition:transform 0.3s ease;transition:transform 0.3s ease;transition:transform 0.3s ease, -webkit-transform 0.3s ease
}
.navbar.open,.navbar.open ~ *{-webkit-transform:translateX(-250px);-ms-transform:translateX(-250px);transform:translateX(-250px)
}
.navbar.open .navbar__burger .bar:nth-child(1),.navbar.open .navbar__burger .bar:nth-child(3){opacity:0
}
.navbar.open .navbar__burger .bar:nth-child(2){-webkit-transform:rotate(45deg) scale(0.7, 0.7);-ms-transform:rotate(45deg) scale(0.7, 0.7);transform:rotate(45deg) scale(0.7, 0.7)
}
.navbar.open .navbar__burger .bar:nth-child(2)::before{content:' ';display:block;position:absolute;top:0;left:0;right:0;bottom:0;background:#ccc;-webkit-transform:rotate(90deg);-ms-transform:rotate(90deg);transform:rotate(90deg)
}
}
