.icon{display:inline-block;vertical-align:middle;background-repeat:no-repeat
}
.icon--twitter{background-image:url("~assets/img/twitter.svg");background-size:24px 19px;width:24px;height:19px
}
.icon--github{background-image:url("~assets/img/github.svg");background-size:22px;width:22px;height:22px
}
.icon--gitlab{background-image:url("~assets/img/gitlab.svg");background-size:22px;width:22px;height:22px
}
.icon--facebook{background-image:url("~assets/img/facebook-logo.svg");background-size:22px;width:22px;height:22px
}
html{font-family:'Source Sans Pro', sans-serif;font-size:100%;min-height:100%;color:#35485E;background-color:#fff;display:-webkit-box;display:-ms-flexbox;display:flex;-webkit-box-orient:vertical;-webkit-box-direction:normal;-ms-flex-direction:column;flex-direction:column
}
body{margin:0;-webkit-box-flex:1;-ms-flex:auto;flex:auto;display:-webkit-box;display:-ms-flexbox;display:flex;-webkit-box-orient:vertical;-webkit-box-direction:normal;-ms-flex-direction:column;flex-direction:column
}
.logo-font{font-family:'Roboto', 'Helvetica', 'Arial', sans-serif;line-height:1;color:#767777;font-weight:500
}
.portfolio{-ms-flex-pack:distribute;justify-content:space-around;display:-webkit-box;display:-ms-flexbox;display:flex;-ms-flex-wrap:wrap;flex-wrap:wrap
}
.portfolio__inner{margin:5px;padding-top:5px
}
@media only screen and (max-width: 640px){
.portfolio__inner{padding:2px
}
}
.porfolio__h2{margin:5px
}
.card{width:95%;height:95%;margin-top:10px
}
