li.item-list__item{margin:0px -30px;list-style:none;text-aline:left
}
.container{margin:0 auto;-webkit-box-flex:1;-ms-flex-positive:1;flex-grow:1;-webkit-box-sizing:border-box;box-sizing:border-box;padding-top:60px;min-height:100vh
}
.container__inner{max-width:1280px;margin:10px auto
}
.post-section{max-width:100%;-webkit-box-flex:1;-ms-flex:1;flex:1
}
.line-section{max-width:60%;margin:20px auto;padding:10px
}
@media only screen and (min-width: 640px) and (max-width: 1024px){
.line-section{max-width:85%
}
}
@media only screen and (min-width: 640px) and (max-width: 800px){
.line-section{max-width:85%
}
}
@media only screen and (max-width: 640px){
.line-section{max-width:95%
}
}
.shop-section{max-width:80%;margin:20px auto;padding:10px
}
@media only screen and (min-width: 640px) and (max-width: 1024px){
.shop-section{max-width:85%
}
}
@media only screen and (min-width: 640px) and (max-width: 800px){
.shop-section{max-width:85%
}
}
@media only screen and (max-width: 640px){
.shop-section{max-width:95%
}
}
h1,h2,h3,h4,p,ul{font-family:"Source Sans Pro",sans-serif;color:#35485E
}
h1{font-size:1.6rem;margin-bottom:10px;pause:40s 60s;font-weight:400;text-align:center
}
@media only screen and (min-width: 640px){
h1{font-size:1.7rem
}
}
@media only screen and (min-width: 1024px){
h1{font-size:1.8rem
}
}
h2{font-size:1.5rem;font-weight:400
}
@media only screen and (min-width: 640px){
h2{size:1.5rem
}
}
@media only screen and (min-width: 1024px){
h2{size:1.7rem
}
}
h3{font-size:1.3rem;font-weight:300
}
@media only screen and (min-width: 640px){
h3{size:1.5rem
}
}
h4{font-size:1.6rem;font-weight:300
}
@media only screen and (min-width: 640px){
h4{size:1.8rem
}
}
p{margin:20px 0 0;font-size:100%;font-weight:300;line-height:1.4
}
@media only screen and (min-width: 640px){
p{font-size:20px
}
}
@media only screen and (min-width: 1024px){
p{font-size:22px
}
}
ul,ol{margin:2px 0 2px;font-size:100%;font-weight:300;line-height:1.4
}
@media only screen and (min-width: 640px){
ul,ol{margin:10px 0 10px;font-size:20px
}
}
@media only screen and (min-width: 1024px){
ul,ol{margin:12px 0 12px;font-size:22px
}
}
li{margin:2px 0 2px
}
@media only screen and (min-width: 640px){
li{margin:4px 0 4px
}
}
@media only screen and (min-width: 1024px){
li{margin:5px 0 5px
}
}
hr{border:none;border-top:1px solid #ddd
}
strong{font-weight:600
}
a{color:red;text-decoration:none
}
a img{border:0px
}
.icon{display:inline-block;vertical-align:middle;background-repeat:no-repeat
}
.icon--twitter{background-image:url("~assets/img/twitter.svg");background-size:24px 19px;width:24px;height:19px
}
.icon--github{background-image:url("~assets/img/github.svg");background-size:22px;width:22px;height:22px
}
.icon--gitlab{background-image:url("~assets/img/gitlab.svg");background-size:22px;width:22px;height:22px
}
.icon--facebook{background-image:url("~assets/img/facebook-logo.svg");background-size:22px;width:22px;height:22px
}
.footer{padding-top:10px;padding-bottom:10px;padding-left:20px;background:#fff
}
.footer__top{padding-bottom:40px
}
@media only screen and (min-width: 640px){
.footer__top{display:-webkit-box;display:-ms-flexbox;display:flex;-ms-flex-pack:distribute;justify-content:space-around
}
}
.footer__list{list-style:none;padding-left:20px;margin:0;font-size:22px
}
.footer__list li{line-height:1.4;font-weight:300
}
.footer__list a{font-weight:400;color:red;text-decoration:none
}
.footer__list.footer__list--small{font-size:20px
}
.footer__header{margin:15px 0;font-size:36px;font-weight:300;color:#35485E
}
.footer__bottom{font-size:20px;font-weight:400;line-height:36px;text-align:center;color:#35485E
}
.footer__bottom a{text-decoration:none;color:#35485E;white-space:nowrap
}
.footer__img{height:24px;display:inline-block;margin-bottom:-3px;margin-left:4px
}
.footer__copyright{font-size:13px;margin-top:0;color:#9D9D9D
}
span.at:before{content:'@'
}
