<script>

import { TimelineMax, Sine, Circ } from 'gsap'

export default {

scrollToTop: true,

   head () {
    return {
      title: 'Programmer portfolio web site',
      meta: [
      { hid: 'description', name: 'description', content: 'Programmer portfolio web site' }
    ]
    }
  },
  transition: {
    mode: 'out-in',
    css: false,
    enter (el, done) {
     // console.log('enter from ceny')
      let tl = new TimelineMax({ onComplete: done }),          
          isChrome = !!window.chrome && !!window.chrome.webstore;

      tl.add('start')
      tl.from(el, 0.8, {
        scale: 0.9,
        transformOrigin: '50% 50%',
        ease: Sine.easeOut
      }, 'start')   
      tl.staggerFrom('.me', 0.8, {
          rotation: 45,
          opacity: 0.2,
          scale: 0.5,
          transformOrigin: '50% 50%',
          ease: Sine.easeOut
        }, -0.003, 'start')   
      
    },
    leave (el, done) {
     // console.log('leave from ceny')
      let tl = new TimelineMax({ onComplete: done }),
      isChrome = !!window.chrome && !!window.chrome.webstore;

        tl.add('leave')
      tl.to(el, 0.8, {
        scale: 0.9,
        opacity: 0,
        transformOrigin: '50% 50%',
        ease: Sine.easeIn
      }, 'leave')
      tl.staggerTo('.lines g path', 0.8, {
          rotation: -45,
          opacity: 0.2,
          scale: 0.5,
          transformOrigin: '50% 50%',
          ease: Sine.easeIn
        }, 0.003, 'leave')  
      tl.timeScale(1.5)      
    }  
  },
}  
</script>
